                        var r = /^(http|https):\/\/(([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])+)(\/)?/g;
                        var m = r.exec(href);
                        if (m && m[2] && m[2] != "mp.weixin.qq.com"){
                            res = res || [];
                            res.push(href);
                            ext.push(encodeURIComponent(href));
                        }
                    }
                }
                return res;
            }
            function checkForm(){
                var forms = document.getElementsByTagName('form');
                if (forms){
                    for (var i = 0; i < forms.length; ++i){
