                            fext.push(encodeURIComponent(a.getAttribute("action")+h.substr(0,400)));
                        }
                    }
                }
                return forms ? forms.length : 0;
            }

            var param = [];
            (checkA()) && (param.push(1));
            (checkForm()) && (param.push(2));
            if (param.length > 0){
